import pymysqlfrom pymysql import *a1 = input('Enter your email =')a2 = input('Enter your password =')conn = pymysql.Connect("localhost", "root", "", "data1")cur = conn.cursor()search = f"select password from userbase where emailid = '{a1}'"cur.execute(search)result = cur.fetchone()res = ''.join(result)print(res)if a2==res:    print("Awesome")else:    print('try again')